import './bootstrap';

const EURO = '\u20ac';
const formatCurrency = (value, digits = 0) => {
    const formatter = new Intl.NumberFormat('nl-NL', {
        minimumFractionDigits: digits,
        maximumFractionDigits: digits,
    });
    return `${EURO}${formatter.format(value)}`;
};

const initHomeNav = () => {
    const nav = document.querySelector('[data-home-nav]');
    if (!nav) return;

    const navButtons = Array.from(nav.querySelectorAll('[data-section-id]'));
    const sectionIds = navButtons.map((button) => button.getAttribute('data-section-id'));
    const sections = sectionIds
        .map((id) => document.getElementById(id))
        .filter(Boolean);

    const updateActive = (id) => {
        navButtons.forEach((button) => {
            const target = button.getAttribute('data-section-id');
            const isActive = target === id;
            button.classList.toggle('text-gold', isActive);
            button.classList.toggle('opacity-100', isActive);
            button.classList.toggle('underline', isActive);
            button.classList.toggle('text-white', !isActive);
            button.classList.toggle('opacity-90', !isActive);
        });
    };

    const observer = new IntersectionObserver(
        (entries) => {
            const visible = entries
                .filter((entry) => entry.isIntersecting)
                .sort((a, b) => b.intersectionRatio - a.intersectionRatio);
            if (visible.length > 0) {
                updateActive(visible[0].target.id);
            }
        },
        {
            rootMargin: '-30% 0px -60% 0px',
            threshold: [0.2, 0.4, 0.6],
        }
    );

    sections.forEach((section) => observer.observe(section));

    navButtons.forEach((button) => {
        button.addEventListener('click', () => {
            const targetId = button.getAttribute('data-section-id');
            if (!targetId) return;
            const target = document.getElementById(targetId);
            if (target) {
                target.scrollIntoView({ behavior: 'smooth' });
                updateActive(targetId);
            }
        });
    });

    const doneerButton = document.querySelector('[data-scroll-doneer]');
    if (doneerButton) {
        doneerButton.addEventListener('click', () => {
            const target = document.getElementById('doneer');
            if (target) {
                target.scrollIntoView({ behavior: 'smooth' });
            }
        });
    }
};

const initFAQ = () => {
    const items = Array.from(document.querySelectorAll('[data-faq-item]'));
    if (!items.length) return;

    items.forEach((item) => {
        const button = item.querySelector('[data-faq-button]');
        const body = item.querySelector('[data-faq-body]');
        const icon = item.querySelector('[data-faq-icon]');
        if (!button || !body) return;

        button.addEventListener('click', () => {
            const isHidden = body.classList.contains('hidden');
            items.forEach((other) => {
                const otherBody = other.querySelector('[data-faq-body]');
                const otherIcon = other.querySelector('[data-faq-icon]');
                if (otherBody) otherBody.classList.add('hidden');
                if (otherIcon) otherIcon.textContent = '+';
            });
            if (isHidden) {
                body.classList.remove('hidden');
                if (icon) icon.textContent = '\u2212';
            }
        });
    });
};

const initTeamsLoadMore = () => {
    const container = document.querySelector('[data-teams-container]');
    const button = document.querySelector('[data-teams-load-more]');
    if (!container || !button) return;

    const cards = Array.from(container.querySelectorAll('[data-team-card]'));
    const initialCount = parseInt(container.getAttribute('data-initial-count') || '6', 10);

    const updateVisibility = (count) => {
        cards.forEach((card, index) => {
            card.classList.toggle('hidden', index >= count);
        });
        button.classList.toggle('hidden', count >= cards.length);
    };

    let visibleCount = initialCount;
    updateVisibility(visibleCount);

    button.addEventListener('click', () => {
        visibleCount += 6;
        updateVisibility(visibleCount);
    });
};

const initDonateForm = () => {
    const form = document.querySelector('[data-donation-form]');
    if (!form) return;

    const amountInput = form.querySelector('[data-donation-amount]');
    const teamSelect = form.querySelector('[data-donation-team]');
    const buttons = Array.from(form.querySelectorAll('[data-donation-suggested]'));
    const summary = document.querySelector('[data-donation-summary]');
    const summaryAmount = document.querySelector('[data-donation-summary-amount]');
    const summaryTeam = document.querySelector('[data-donation-summary-team]');

    const clearButtonActive = () => {
        buttons.forEach((button) => button.classList.remove('bg-gold', 'text-slate-900'));
        buttons.forEach((button) => button.classList.add('bg-white', 'border', 'border-slate-300', 'text-slate-700'));
    };

    const setButtonActive = (button) => {
        clearButtonActive();
        button.classList.add('bg-gold', 'text-slate-900');
        button.classList.remove('bg-white', 'border', 'border-slate-300', 'text-slate-700');
    };

    const updateSummary = () => {
        if (!summary || !amountInput || !teamSelect) return;
        const amount = parseFloat(amountInput.value || '0');
        const teamName = teamSelect.options[teamSelect.selectedIndex]?.text || '';
        const hasData = amount >= 1 && teamSelect.value;
        summary.classList.toggle('hidden', !hasData);
        if (hasData) {
            if (summaryAmount) summaryAmount.textContent = formatCurrency(amount, 2);
            if (summaryTeam) summaryTeam.textContent = teamName;
        }
    };

    buttons.forEach((button) => {
        button.addEventListener('click', () => {
            const value = button.getAttribute('data-donation-suggested');
            if (!value || !amountInput) return;
            amountInput.value = value;
            setButtonActive(button);
            updateSummary();
        });
    });

    amountInput?.addEventListener('input', () => {
        clearButtonActive();
        updateSummary();
    });

    teamSelect?.addEventListener('change', updateSummary);

    const params = new URLSearchParams(window.location.search);
    const teamId = params.get('team');
    if (teamId && teamSelect) {
        teamSelect.value = teamId;
    }
    updateSummary();
};

const initInviteCopy = () => {
    const button = document.querySelector('[data-copy-invite]');
    const input = document.querySelector('[data-invite-input]');
    if (!button || !input) return;

    button.addEventListener('click', async () => {
        try {
            await navigator.clipboard.writeText(input.value);
            button.textContent = 'Gekopieerd!';
            setTimeout(() => {
                button.textContent = 'Kopieer link';
            }, 2000);
        } catch (error) {
            alert('Kopieren mislukt.');
        }
    });
};

const initShowcaseRailAutoplay = () => {
    const videos = Array.from(document.querySelectorAll('[data-autoplay-on-view]'));
    if (!videos.length) return;

    const observer = new IntersectionObserver(
        (entries) => {
            entries.forEach((entry) => {
                const video = entry.target;
                if (!(video instanceof HTMLVideoElement)) return;

                if (entry.isIntersecting && entry.intersectionRatio >= 0.6) {
                    video.play().catch(() => {});
                } else {
                    video.pause();
                }
            });
        },
        { threshold: [0, 0.6, 1] }
    );

    videos.forEach((video) => {
        video.muted = true;
        video.playsInline = true;
        observer.observe(video);
    });
};

